import { useState } from "react";

export function Rating() {
  const [count, setRating] = useState(0);

  const plus = () => {
    setRating(count + 1);
  };
  const minus = () => {
    setRating(count - 1);
  };
  return (
    <>
      <h3>Please rate the article</h3>
      <p>Rating: {count} </p>
